mapper.select:
SELECT  id, disintegrate_plant_id, car_info_id, register_time, register_user_id, is_register, query_result,
query_time, query_user_id, is_query, verify_result, verify_time, verify_user_id, record_number, recycle_number,
is_verify, data_upload_time, data_uploaduser_id, is_data_upload, dismantle_time, dismantle_user_id, is_dismantle,
pic_upload_time, pic_upload_user_id, is_pic_upload, print_recycle_time, print_recycle_user_id, is_print_recycle,
is_logout, logout_result, logout_time, logout_user_id, is_appoint_time, is_appoint_userid, is_appoint_logout_time,
procedure_issue_time, procedure_issue_user_id, register_address, is_procedure_issue, grant_car_receipt_user_id,
grant_car_receipt_time, is_grant_car_receipt, print_car_ledger_user_id, print_car_ledger_time, is_print_car_ledger,
print_output_user_id, printout_put_time, is_printout_put, print_cancel_prove_user_id, print_cancel_prove_time,
print_count, print_ledger_batch, is_print_cancel_prove, is_supervise_sale, supervise_sale_user, sale_of_affirm_time,
is_dismantle_affirm, dismantle_affirm_time, dismantle_affirm_user_id, is_delete
FROM ts_car_processing


        AND id=#{id}  


        AND disintegrate_plant_id=#{disintegratePlantId}  


        AND car_info_id=#{carInfoId}  


        AND register_time=#{registerTime}  


        AND register_user_id=#{registerUserId}  


        AND is_register=#{isRegister}  


        AND query_result=#{queryResult}  


        AND query_time=#{queryTime}  


        AND query_user_id=#{queryUserId}  


        AND is_query=#{isQuery}  


        AND verify_result=#{verifyResult}  


        AND verify_time=#{verifyTime}  


        AND verify_user_id=#{verifyUserId}  


        AND record_number=#{recordNumber}  


        AND recycle_number=#{recycleNumber}  


        AND is_verify=#{isVerify}  


        AND data_upload_time=#{dataUploadTime}  


        AND data_uploaduser_id=#{dataUploaduserId}  


        AND is_data_upload=#{isDataUpload}  


        AND dismantle_time=#{dismantleTime}  


        AND dismantle_user_id=#{dismantleUserId}  


        AND is_dismantle=#{isDismantle}  


        AND pic_upload_time=#{picUploadTime}  


        AND pic_upload_user_id=#{picUploadUserId}  


        AND is_pic_upload=#{isPicUpload}  


        AND print_recycle_time=#{printRecycleTime}  


        AND print_recycle_user_id=#{printRecycleUserId}  


        AND is_print_recycle=#{isPrintRecycle}  


        AND is_logout=#{isLogout}  


        AND logout_result=#{logoutResult}  


        AND logout_time=#{logoutTime}  


        AND logout_user_id=#{logoutUserId}  


        AND is_appoint_time=#{isAppointTime}  


        AND is_appoint_userid=#{isAppointUserid}  


        AND is_appoint_logout_time=#{isAppointLogoutTime}  


        AND procedure_issue_time=#{procedureIssueTime}  


        AND procedure_issue_user_id=#{procedureIssueUserId}  


        AND register_address=#{registerAddress}  


        AND is_procedure_issue=#{isProcedureIssue}  


        AND grant_car_receipt_user_id=#{grantCarReceiptUserId}  


        AND grant_car_receipt_time=#{grantCarReceiptTime}  


        AND is_grant_car_receipt=#{isGrantCarReceipt}  


        AND print_car_ledger_user_id=#{printCarLedgerUserId}  


        AND print_car_ledger_time=#{printCarLedgerTime}  


        AND is_print_car_ledger=#{isPrintCarLedger}  


        AND print_output_user_id=#{printOutputUserId}  


        AND printout_put_time=#{printoutPutTime}  


        AND is_printout_put=#{isPrintoutPut}  


        AND print_cancel_prove_user_id=#{printCancelProveUserId}  


        AND print_cancel_prove_time=#{printCancelProveTime}  


        AND print_count=#{printCount}  


        AND print_ledger_batch=#{printLedgerBatch}  


        AND is_print_cancel_prove=#{isPrintCancelProve}  


        AND is_supervise_sale=#{isSuperviseSale}  


        AND supervise_sale_user=#{superviseSaleUser}  


        AND sale_of_affirm_time=#{saleOfAffirmTime}  


        AND is_dismantle_affirm=#{isDismantleAffirm}  


        AND dismantle_affirm_time=#{dismantleAffirmTime}  


        AND dismantle_affirm_user_id=#{dismantleAffirmUserId}  


        AND is_delete=#{isDelete}

