mapper.select:
SELECT IFNULL(i.id,'') AS id, IFNULL(i.car_code,'') AS carCode, IFNULL(i.car_no,'') AS carNo, IFNULL(d.vin,'') AS vin, IFNULL(e.approach_time,'') AS approachTime, IFNULL(e.is_approach,'') AS isApproach, IFNULL(e.is_initial_survey,'') AS isInitialSurvey, IFNULL(e.is_pretreatment,'') AS isPretreatment, IFNULL(e.is_copy_number,'') AS isCopyNumber, IFNULL(p.is_register,'') AS isRegister, IFNULL(p.is_query,'') AS isQuery, IFNULL(p.is_verify,'') AS isVerify, IFNULL(p.is_destructive,'') AS isDestructive, IFNULL(p.is_data_upload,'') AS isDataUpload, IFNULL(p.is_dismantle,'') AS isDismantle, IFNULL(p.is_logout,'') AS isLogout, IFNULL(p.is_appoint_logout_time,'') AS isAppointLogoutTime, IFNULL(s.is_get_salvage,'') AS isGetSalvage, IFNULL(p.verify_result,'') AS verifyResult FROM ts_car_info AS i LEFT JOIN ts_car_identity AS d ON i.id = d.car_info_id LEFT JOIN ts_car_enter AS e ON i.id = e.car_info_id LEFT JOIN ts_car_processing AS p ON i.id = p.car_info_id LEFT JOIN ts_car_salvage AS s ON i.id = s.car_info_id WHERE i.disintegrate_plant_id = #{disintegratePlantId} AND i.car_source = #{id}
AND CONCAT(i.car_code,i.car_no,i.car_name) LIKE CONCAT('%',#{findMsg},'%')
/*待入场*/ AND e.is_approach = 1
/*待核档状态*/ AND p.is_query = 2 AND p.is_verify = 1
/*待商委注销状态*/ AND p.is_logout = 2 AND p.is_appoint_logout_time = 1
/*待领取残值状态*/ AND p.is_appoint_logout_time = 2 AND s.is_get_salvage = 1
/*报废成功*/ AND p.is_logout = 2 AND p.is_appoint_logout_time = 2
/*核档未通过*/ AND p.is_query = 2 AND p.is_verify = 3

mapper.update:
UPDATE ts_car_enter SET
	is_approach = #{isApproach},
	approach_time = #{approachTime},
	approach_user_id = #{approachUserId},
	is_initial_survey = #{isInitialSurvey}
WHERE
	id = #{id}
	AND disintegrate_plant_id = #{disintegratePlantId}

mapper.select:
SELECT IFNULL( i.id, '' ) AS id, IFNULL( i.car_code, '' ) AS carCode, IFNULL( e.approach_time, '' ) AS approachTime, IFNULL( i.car_no, '' ) AS carNo, IFNULL( i.car_name, '' ) AS carName, IFNULL( i.contacts, '' ) AS contacts, IFNULL( i.contacts_phone, '' ) AS contactsPhone, IFNULL( d.vin, '' ) AS vin, IFNULL( e.is_approach, '' ) AS isApproach, IFNULL( e.is_initial_survey, '' ) AS isInitialSurvey, IFNULL( e.soft_print, '' ) AS softPrint, IFNULL( e.is_pretreatment, '' ) AS isPretreatment, IFNULL( e.is_copy_number, '' ) AS isCopyNumber, IFNULL( s.dismantling_way, '' ) AS dismantleWay, IFNULL( CONCAT(i.car_location_area,i.car_location_row, i.car_location_column, i.car_location_number), '' ) AS carLocation FROM ts_car_info AS i LEFT JOIN ts_car_enter AS e ON i.id = e.car_info_id LEFT JOIN ts_car_identity AS d ON i.id = d.car_info_id LEFT JOIN ts_car_survey AS s ON i.id = s.car_info_id WHERE i.disintegrate_plant_id = #{disintegratePlantId} /*待入场*/ AND e.is_approach = 1
/*待初检*/ AND e.is_approach = 2 AND e.is_initial_survey = 1
/*待预处理*/ AND e.is_initial_survey = 2 AND e.is_pretreatment = 1
/*拆解方式未确定*/ AND e.dismantle_way = 5
AND CONCAT(i.contacts,i.contacts_phone,i.car_no,i.car_name) LIKE CONCAT('%',#{findMsg},'%')

SELECT
    MAX( code_after ) AS codeAfterMax
FROM
    ts_car_info
WHERE
    code_front = #{codeFront}
    AND code_in = #{codeIn}
    AND disintegrate_plant_id = #{disintegratePlantId}

SELECT
	COUNT(id)
FROM ts_car_info WHERE car_no = #{carNo} AND disintegrate_plant_id = #{disintegratePlantId}

SELECT IFNULL( i.car_no, '' ) AS carNo, IFNULL( i.car_name, '' ) AS carName, IFNULL( i.car_code, '' ) AS carCode, IFNULL( i.id, '' ) AS id, IFNULL( i.contacts, '' ) AS contacts, IFNULL( i.contacts_phone, '' ) AS contactsPhone, IFNULL( e.approach_time, '' ) AS approachTime FROM ts_car_info AS i LEFT JOIN ts_car_enter AS e ON i.id = e.car_info_id LEFT JOIN ts_car_processing AS p ON i.id = p.car_info_id LEFT JOIN ts_car_salvage AS s ON i.id = s.car_info_id LEFT JOIN ts_car_survey AS u ON i.id = u.car_info_id LEFT JOIN ts_car_procedure_log AS g ON i.id = g.car_info_id WHERE i.disintegrate_plant_id = #{disintegratePlantId} /*待初检*/ AND e.is_approach = 2 AND e.is_initial_survey = 1
/*待打印软牌*/ AND e.is_initial_survey = 2 AND e.soft_print = 1
/*待预处理*/ AND e.is_initial_survey = 2 AND e.is_pretreatment = 1
/*待拓号*/ AND e.is_pretreatment = 2 AND e.is_copy_number = 1
/*待确定拆解方式*/ AND u.dismantling_way = 5
/*待登记*/ AND e.is_copy_number = 2 AND p.is_register = 1
/*待查询*/ AND p.is_register = 2 AND p.is_query = 1
/*待核档*/ AND p.is_query = 2 AND p.is_verify = 1
/*待拆解*/ AND p.is_destructive = 2 AND p.is_dismantle = 1
/*待上传商委数据*/ AND p.is_destructive = 2 AND p.is_data_upload = 1
/*待打印回收证明*/ AND p.is_data_upload = 2 AND p.is_print_recycle = 1
/*待录入注销时间*/ AND p.is_print_recycle = 2 AND p.is_logout = 1
/*待录入商委注销时间*/ AND p.is_logout = 2 AND p.is_appoint_logout_time = 1
/*待处理手续异常*/ AND (p.is_query = 3 OR p.is_verify = 3) AND (g.type = 1 OR g.type = 2) AND g.state = 1
/*待残值发放*/ AND p.is_appoint_logout_time = 2 AND s.is_get_salvage = 1

 SELECT
    IFNULL(p.file_name, '') AS fileName,
	IFNULL(p.file_url, '') AS fileUrl
FROM
	ts_car_info AS i
	LEFT JOIN ts_car_pic AS p ON i.id = p.car_info_id
WHERE
	i.disintegrate_plant_id = #{disintegratePlantId}
	AND p.car_info_id = #{carInfoId}
	AND p.first_type = #{firstType}
