mapper.update:
       UPDATE user
        SET login_name = #{user.login_name},
				user_name=#{user.user_name},
 				phone=#{user.phone},
				operator=#{user.operator}
        where id=#{user.id}

       UPDATE user
        SET
				user_password=#{user.user_password}

        where id=#{user.id}

UPDATE department_user
 SET department_id = #{user.depart_id}
 where user_id=#{user.id}
